const sideNav=document.querySelectorAll(".sidenav");M.Sidenav.init(sideNav,{inDuration:650,outDuration:500});const slider=document.querySelectorAll(".slider");M.Slider.init(slider,{indicators:!0,transition:600,interval:3e3});const parallax=document.querySelectorAll(".parallax");M.Parallax.init(parallax);const materialbox=document.querySelectorAll(".materialboxed");M.Materialbox.init(materialbox);const scrollspy=document.querySelectorAll(".scrollspy");M.ScrollSpy.init(scrollspy,{scrollOffset:65});const fixedActionBtn=document.querySelectorAll(".fixed-action-btn");M.FloatingActionButton.init(fixedActionBtn,{toolbarEnabled:!0});const scrollEffect=()=>{const t=document.querySelectorAll(".scale-transition");let e=window.innerHeight/1.1;for(i=0;i<t.length;i++){t[i].getBoundingClientRect().top<e?t[i].classList.remove("scale-out"):t[i].classList.add("scale-out")}},scrollEffectBackToTop=()=>{const t=document.querySelector(".custom-backtotop-btn");document.getElementById("home").getBoundingClientRect().top<window.innerHeight/1.1?(t.classList.add("scale-in"),t.classList.remove("scale-out")):(t.classList.add("scale-out"),t.classList.remove("scale-in"))};window.addEventListener("scroll",scrollEffect),window.addEventListener("scroll",scrollEffectBackToTop);